INSERT INTO `news` VALUES (1,'Начало',2,'Решил перенести свой первый проект на фреймворк Laravel. Развернул проект, подготовил базу и перенес таблицу с программами. Создал все модели и таблицы для остальных сущностей. Сделал контроллер и экшен-класс с поиском, страница с поиском.','2022-11-05 21:56:45','2022-11-09 08:01:21',1,NULL),(2,'Второй день',2,'Настроил политики доступа к модели программы. Т.е. смотреть программу могут все, а редактировать и удалять -  только автор или администратор. Страница вывода единичной программы. Логин по табельному номеру и паролю.','2022-11-06 21:56:45','2022-11-05 21:56:47',1,NULL),(3,'Третий день',2,'Много всего добалено было. Добавление программ, удаление, редактирование. Валидация полей на уровне запросов. Добавил админку для редактирования пользователей и их ролей. Должности и смены.  ','2022-11-07 21:56:45','2022-11-05 21:56:47',1,NULL),(4,'День четвертый',2,'В админской части реализовал работу с Оборудованием, сменами, должностями и новостями. \r\nНа публичной части сделал страницу профиля пользователя с краткой статистикой и возможность выбора глобальной темы оформления.','2022-11-05 21:56:45','2022-11-09 08:03:31',1,NULL);
